// QuickActionService stub
class QuickActionService {
    fun scheduleNoteReminder(noteId: Long, reminderTime: Long, context: Context) {
        // Use WorkManager to schedule reminder notification
        val workRequest = androidx.work.OneTimeWorkRequestBuilder<ReminderWorker>()
            .setInitialDelay(reminderTime - System.currentTimeMillis(), java.util.concurrent.TimeUnit.MILLISECONDS)
            .setInputData(androidx.work.workDataOf("noteId" to noteId))
            .build()
        
        androidx.work.WorkManager.getInstance(context).enqueue(workRequest)
    }
}

class NoteEditorActivity : ComponentActivity() {
    
    private val viewModel by viewModels<NoteEditorViewModel> {
        NoteEditorViewModelFactory(
            (application as com.ainotebuddy.app.AINoteBuddyApplication).advancedNoteRepository,
            this
        )
    }
    
    private val cameraPermissionLauncher = registerForActivityResult(
        ActivityResultContracts.RequestPermission()
    ) { isGranted ->
        if (isGranted) {
            openCamera()
        }
    }
    
    private val cameraLauncher = registerForActivityResult(
        ActivityResultContracts.TakePicture()
    ) { success ->
        if (success) {
            // Image captured successfully
        }
    }
    
    private val galleryLauncher = registerForActivityResult(
        ActivityResultContracts.GetContent()
    ) { uri ->
        uri?.let { viewModel.addImageToNote(it) }
    }
    
    private val voiceRecognitionLauncher = registerForActivityResult(
        ActivityResultContracts.StartActivityForResult()
    ) { result ->
        if (result.resultCode == RESULT_OK) {
            val spokenText = result.data?.getStringArrayListExtra(
                android.speech.RecognizerIntent.EXTRA_RESULTS
            )?.firstOrNull()
            spokenText?.let { viewModel.processVoiceInput(it) }
        }
    }
    
    private val importNoteLauncher = registerForActivityResult(ActivityResultContracts.OpenDocument()) { uri: Uri? ->
        uri?.let {
            contentResolver.openInputStream(it)?.bufferedReader()?.use { reader ->
                val content = reader.readText()
                // Create a new note with imported content
                val newNote = com.ainotebuddy.app.data.NoteEntity(
                    title = "Imported Note",
                    content = content,
                    createdAt = System.currentTimeMillis(),
                    updatedAt = System.currentTimeMillis()
                )
                viewModel.addNote(newNote, this)
            }
        }
    }
    
    private val exportNoteLauncher = registerForActivityResult(ActivityResultContracts.CreateDocument("text/plain")) { uri: Uri? ->
        uri?.let {
            contentResolver.openOutputStream(it)?.use { outputStream ->
                val note = viewModel.note.value
                val content = "Title: ${note.title}\n\n${note.content}"
                outputStream.write(content.toByteArray())
            }
        }
    }
    
    private val pdfExportLauncher = registerForActivityResult(ActivityResultContracts.CreateDocument("application/pdf")) { uri: Uri? ->
        uri?.let {
            exportToPdf(it)
        }
    }
    
    private val reminderLauncher = registerForActivityResult(ActivityResultContracts.StartActivityForResult()) { result ->
        if (result.resultCode == RESULT_OK) {
            val reminderTime = result.data?.getLongExtra("reminder_time", 0L) ?: 0L
            if (reminderTime > 0) {
                viewModel.setReminder(viewModel.note.value.id, reminderTime)
            }
        }
    }
    
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        
        val noteId = intent.getLongExtra("note_id", -1L)
        if (noteId != -1L) {
            viewModel.loadNote(noteId)
        }
        
        setContent {
            AINoteBuddyTheme {
                Surface(
                    modifier = Modifier.fillMaxSize(),
                    color = MaterialTheme.colorScheme.background
                ) {
                    NoteEditorScreen(
                        viewModel = viewModel,
                        onBackPressed = { finish() },
                        onSave = { viewModel.saveNote() },
                        onCameraClick = { requestCameraPermission() },
                        onGalleryClick = { galleryLauncher.launch("image/*") },
                        onVoiceClick = { startVoiceRecognition() },
                        onImportClick = { importNoteLauncher.launch(arrayOf("text/*", "application/json")) },
                        onExportClick = { exportNoteLauncher.launch("note.txt") },
                        onPdfExportClick = { pdfExportLauncher.launch("note.pdf") },
                        onReminderClick = { showReminderDialog() },
                        onDrawingModeToggle = { viewModel.toggleDrawingMode() },
                        onSmartAssistClick = { viewModel.showSmartAssist() }
                    )
                }
            }
        }
    }
    
    private fun requestCameraPermission() {
        when {
            ContextCompat.checkSelfPermission(this, Manifest.permission.CAMERA) == PackageManager.PERMISSION_GRANTED -> {
                openCamera()
            }
            else -> {
                cameraPermissionLauncher.launch(Manifest.permission.CAMERA)
            }
        }
    }
    
    private fun openCamera() {
        val photoFile = java.io.File(
            getExternalFilesDir(Environment.DIRECTORY_PICTURES),
            "photo_${System.currentTimeMillis()}.jpg"
        )
        val photoUri = FileProvider.getUriForFile(
            this,
            "${packageName}.fileprovider",
            photoFile
        )
        cameraLauncher.launch(photoUri)
    }
    
    private fun startVoiceRecognition() {
        val intent = Intent(android.speech.RecognizerIntent.ACTION_RECOGNIZE_SPEECH).apply {
            putExtra(android.speech.RecognizerIntent.EXTRA_LANGUAGE_MODEL, android.speech.RecognizerIntent.LANGUAGE_MODEL_FREE_FORM)
            putExtra(android.speech.RecognizerIntent.EXTRA_PROMPT, "Speak your note...")
        }
        voiceRecognitionLauncher.launch(intent)
    }
    
    private fun exportToPdf(uri: Uri) {
        val note = viewModel.note.value
        val document = PdfDocument()
        val pageInfo = PdfDocument.PageInfo.Builder(595, 842, 1).create()
        val page = document.startPage(pageInfo)
        val canvas = page.canvas
        val paint = Paint().apply {
            color = android.graphics.Color.BLACK
            textSize = 12f
        }
        
        canvas.drawText("Title: ${note.title}", 50f, 50f, paint)
        canvas.drawText(note.content, 50f, 100f, paint)
        
        document.finishPage(page)
        contentResolver.openOutputStream(uri)?.use { outputStream ->
            document.writeTo(outputStream)
        }
        document.close()
    }
    
    private fun showReminderDialog() {
        // Show date and time picker dialog
        val calendar = java.util.Calendar.getInstance()
        DatePickerDialog(
            this,
            { _, year, month, day ->
                TimePickerDialog(
                    this,
                    { _, hour, minute ->
                        calendar.set(year, month, day, hour, minute)
                        viewModel.setReminder(viewModel.note.value.id, calendar.timeInMillis)
                    },
                    calendar.get(java.util.Calendar.HOUR_OF_DAY),
                    calendar.get(java.util.Calendar.MINUTE),
                    true
                ).show()
            },
            calendar.get(java.util.Calendar.YEAR),
            calendar.get(java.util.Calendar.MONTH),
            calendar.get(java.util.Calendar.DAY_OF_MONTH)
        ).show()
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun NoteEditorScreen(
    viewModel: NoteEditorViewModel,
    onBackPressed: () -> Unit,
    onSave: () -> Unit,
    onCameraClick: () -> Unit,
    onGalleryClick: () -> Unit,
    onVoiceClick: () -> Unit,
    onImportClick: () -> Unit,
    onExportClick: () -> Unit,
    onPdfExportClick: () -> Unit,
    onReminderClick: () -> Unit,
    onDrawingModeToggle: () -> Unit,
    onSmartAssistClick: () -> Unit
) {
    val note by viewModel.note.collectAsState()
    val isSaving by viewModel.isSaving.collectAsState()
    val isDrawingMode by viewModel.isDrawingMode.collectAsState()
    val checklistItems by viewModel.checklistItems.collectAsState()
    val images by viewModel.images.collectAsState()
    val pdfUris by viewModel.pdfUris.collectAsState()
    
    // Smart Assist states
    val isSmartAssistVisible by viewModel.isSmartAssistVisible.collectAsState()
    val aiResponse by viewModel.aiResponse.collectAsState()
    val isAIProcessing by viewModel.isAIProcessing.collectAsState()
    
    var showFormattingOptions by remember { mutableStateOf(false) }
    var showAdvancedOptions by remember { mutableStateOf(false) }
    var textFieldValue by remember { mutableStateOf(androidx.compose.ui.text.input.TextFieldValue(note.content)) }
    
    // Sync textFieldValue with note content changes
    LaunchedEffect(note.content) {
        if (textFieldValue.text != note.content) {
            textFieldValue = textFieldValue.copy(text = note.content)
        }
    }
    
    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Edit Note") },
                navigationIcon = {
                    IconButton(onClick = onBackPressed) {
                        Icon(Icons.Filled.ArrowBack, contentDescription = "Back")
                    }
                },
                actions = {
                    IconButton(onClick = onSave) {
                        if (isSaving) {
                            CircularProgressIndicator(
                                modifier = Modifier.size(24.dp),
                                strokeWidth = 2.dp
                            )
                        } else {
                            Icon(Icons.Filled.Save, contentDescription = "Save")
                        }
                    }
                }
            )
        },
        bottomBar = {
            NoteEditorBottomBar(
                onFormattingClick = { showFormattingOptions = !showFormattingOptions },
                onCameraClick = onCameraClick,
                onGalleryClick = onGalleryClick,
                onVoiceClick = onVoiceClick,
                onDrawingClick = onDrawingModeToggle,
                isDrawingMode = isDrawingMode,
                onAdvancedClick = { showAdvancedOptions = !showAdvancedOptions }
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = onSmartAssistClick,
                containerColor = MaterialTheme.colorScheme.primary,
                contentColor = MaterialTheme.colorScheme.onPrimary
            ) {
                Icon(
                    imageVector = Icons.Default.SmartToy,
                    contentDescription = "Smart Assist",
                    modifier = Modifier.size(24.dp)
                )
            }
        }
    ) { paddingValues ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(paddingValues)
        ) {
            // Title field
            OutlinedTextField(
                value = note.title,
                onValueChange = { viewModel.updateTitle(it) },
                label = { Text("Title") },
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                textStyle = MaterialTheme.typography.headlineSmall
            )
            
            // Content field
            OutlinedTextField(
                value = note.content,
                onValueChange = { viewModel.updateContent(it) },
                label = { Text("Content") },
                modifier = Modifier
                    .fillMaxWidth()
                    .weight(1f)
                    .padding(horizontal = 16.dp),
                textStyle = MaterialTheme.typography.bodyLarge,
                minLines = 10
            )
            
            // Images section
            if (images.isNotEmpty()) {
                LazyRow(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(16.dp),
                    horizontalArrangement = Arrangement.spacedBy(8.dp)
                ) {
                    items(images) { imageUri ->
                        AsyncImage(
                            model = imageUri,
                            contentDescription = "Note image",
                            modifier = Modifier
                                .size(80.dp)
                                .clip(RoundedCornerShape(8.dp))
                        )
                    }
                }
            }
            
            // Checklist section
            if (checklistItems.isNotEmpty()) {
                Column(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(16.dp)
                ) {
                    Text(
                        text = "Checklist",
                        style = MaterialTheme.typography.titleMedium,
                        fontWeight = FontWeight.Bold
                    )
                    Spacer(modifier = Modifier.height(8.dp))
                    checklistItems.forEach { item ->
                        Row(
                            modifier = Modifier.fillMaxWidth(),
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            Checkbox(
                                checked = item.isChecked,
                                onCheckedChange = { viewModel.toggleChecklistItemChecked(item) }
                            )
                            Text(
                                text = item.text,
                                modifier = Modifier.weight(1f),
                                style = MaterialTheme.typography.bodyMedium
                            )
                        }
                    }
                }
            }
        }
        
        // Formatting options dialog
        if (showFormattingOptions) {
            FormattingOptionsDialog(
                onDismiss = { showFormattingOptions = false },
                onBold = { 
                    val selection = textFieldValue.selection
                    val selectedText = textFieldValue.text.substring(selection.start, selection.end)
                    val newText = textFieldValue.text.replaceRange(selection.start, selection.end, "**$selectedText**")
                    textFieldValue = textFieldValue.copy(text = newText)
                },
                onItalic = { 
                    val selection = textFieldValue.selection
                    val selectedText = textFieldValue.text.substring(selection.start, selection.end)
                    val newText = textFieldValue.text.replaceRange(selection.start, selection.end, "*$selectedText*")
                    textFieldValue = textFieldValue.copy(text = newText)
                },
                onUnderline = { 
                    val selection = textFieldValue.selection
                    val selectedText = textFieldValue.text.substring(selection.start, selection.end)
                    val newText = textFieldValue.text.replaceRange(selection.start, selection.end, "<u>$selectedText</u>")
                    textFieldValue = textFieldValue.copy(text = newText)
                },
                onBulletList = { 
                    val cursorPos = textFieldValue.selection.start
                    val lines = textFieldValue.text.split('\n')
                    var currentPos = 0
                    var lineIndex = 0
                    
                    for (i in lines.indices) {
                        if (currentPos + lines[i].length >= cursorPos) {
                            lineIndex = i
                            break
                        }
                        currentPos += lines[i].length + 1
                    }
                    
                    val newLines = lines.toMutableList()
                    newLines[lineIndex] = "‚Ä¢ ${lines[lineIndex]}"
                    textFieldValue = textFieldValue.copy(text = newLines.joinToString("\n"))
                },
                onCheckbox = { 
                    val cursorPos = textFieldValue.selection.start
                    val lines = textFieldValue.text.split('\n')
                    var currentPos = 0
                    var lineIndex = 0
                    
                    for (i in lines.indices) {
                        if (currentPos + lines[i].length >= cursorPos) {
                            lineIndex = i
                            break
                        }
                        currentPos += lines[i].length + 1
                    }
                    
                    val newLines = lines.toMutableList()
                    newLines[lineIndex] = "- [ ] ${lines[lineIndex]}"
                    textFieldValue = textFieldValue.copy(text = newLines.joinToString("\n"))
                },
                onCode = { 
                    val selection = textFieldValue.selection
                    val selectedText = textFieldValue.text.substring(selection.start, selection.end)
                    val newText = if (selectedText.contains('\n')) {
                        textFieldValue.text.replaceRange(selection.start, selection.end, "```\n$selectedText\n```")
                    } else {
                        textFieldValue.text.replaceRange(selection.start, selection.end, "`$selectedText`")
                    }
                    textFieldValue = textFieldValue.copy(text = newText)
                }
            )
        }
        
        // Advanced options dialog
        if (showAdvancedOptions) {
            AdvancedOptionsDialog(
                onDismiss = { showAdvancedOptions = false },
                onImport = onImportClick,
                onExport = onExportClick,
                onPdfExport = onPdfExportClick,
                onReminder = onReminderClick
            )
        }
        
        // Smart Assist Dialog
        SmartAssistDialog(
            isVisible = isSmartAssistVisible,
            noteTitle = note.title,
            noteContent = note.content,
            onDismiss = { viewModel.hideSmartAssist() },
            onAIRequest = { request -> viewModel.processAIRequest(request) },
            aiResponse = aiResponse,
            isProcessing = isAIProcessing,
            onApplyResponse = { response -> viewModel.applyAIResponse(response) }
        )
    }
}

@Composable
fun NoteEditorBottomBar(
    onFormattingClick: () -> Unit,
    onCameraClick: () -> Unit,
    onGalleryClick: () -> Unit,
    onVoiceClick: () -> Unit,
    onDrawingClick: () -> Unit,
    isDrawingMode: Boolean,
    onAdvancedClick: () -> Unit
) {
    Surface(
        modifier = Modifier.fillMaxWidth(),
        color = MaterialTheme.colorScheme.surface,
        tonalElevation = 8.dp
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            horizontalArrangement = Arrangement.SpaceEvenly
        ) {
            IconButton(onClick = onFormattingClick) {
                Icon(Icons.Filled.Edit, contentDescription = "Formatting")
            }
            IconButton(onClick = onCameraClick) {
                Icon(Icons.Filled.CameraAlt, contentDescription = "Camera")
            }
            IconButton(onClick = onGalleryClick) {
                Icon(Icons.Filled.Image, contentDescription = "Gallery")
            }
            IconButton(onClick = onVoiceClick) {
                Icon(Icons.Filled.Mic, contentDescription = "Voice")
            }
            IconButton(onClick = onDrawingClick) {
                Icon(
                    Icons.Filled.Brush,
                    contentDescription = "Drawing",
                    tint = if (isDrawingMode) MaterialTheme.colorScheme.primary else MaterialTheme.colorScheme.onSurface
                )
            }
            IconButton(onClick = onAdvancedClick) {
                Icon(Icons.Filled.MoreVert, contentDescription = "More options")
            }
        }
    }
}

@Composable
fun FormattingOptionsDialog(
    onDismiss: () -> Unit,
    onBold: () -> Unit,
    onItalic: () -> Unit,
    onUnderline: () -> Unit,
    onBulletList: () -> Unit,
    onCheckbox: () -> Unit,
    onCode: () -> Unit
) {
    AlertDialog(
        onDismissRequest = onDismiss,
        title = { Text("Formatting Options") },
        text = {
            Column {
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.SpaceEvenly
                ) {
                    IconButton(onClick = onBold) {
                        Icon(Icons.Filled.FormatBold, contentDescription = "Bold")
                    }
                    IconButton(onClick = onItalic) {
                        Icon(Icons.Filled.FormatItalic, contentDescription = "Italic")
                    }
                    IconButton(onClick = onUnderline) {
                        Icon(Icons.Filled.FormatUnderlined, contentDescription = "Underline")
                    }
                }
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.SpaceEvenly
                ) {
                    IconButton(onClick = onBulletList) {
                        Icon(Icons.Filled.FormatListBulleted, contentDescription = "Bullet List")
                    }
                    IconButton(onClick = onCheckbox) {
                        Icon(Icons.Filled.CheckBox, contentDescription = "Checkbox")
                    }
                    IconButton(onClick = onCode) {
                        Icon(Icons.Filled.Code, contentDescription = "Code")
                    }
                }
            }
        },
        confirmButton = {
            TextButton(onClick = onDismiss) {
                Text("Close")
            }
        }
    )
}

@Composable
fun AdvancedOptionsDialog(
    onDismiss: () -> Unit,
    onImport: () -> Unit,
    onExport: () -> Unit,
    onPdfExport: () -> Unit,
    onReminder: () -> Unit
) {
    AlertDialog(
        onDismissRequest = onDismiss,
        title = { Text("Advanced Options") },
        text = {
            Column(
                verticalArrangement = Arrangement.spacedBy(8.dp)
            ) {
                Button(
                    onClick = onImport,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Icon(Icons.Filled.Upload, contentDescription = null)
                    Spacer(modifier = Modifier.width(8.dp))
                    Text("Import Note")
                }
                Button(
                    onClick = onExport,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Icon(Icons.Filled.Download, contentDescription = null)
                    Spacer(modifier = Modifier.width(8.dp))
                    Text("Export as Text")
                }
                Button(
                    onClick = onPdfExport,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Icon(Icons.Filled.PictureAsPdf, contentDescription = null)
                    Spacer(modifier = Modifier.width(8.dp))
                    Text("Export as PDF")
                }
                Button(
                    onClick = onReminder,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Icon(Icons.Filled.Event, contentDescription = null)
                    Spacer(modifier = Modifier.width(8.dp))
                    Text("Set Reminder")
                }
            }
        },
        confirmButton = {
            TextButton(onClick = onDismiss) {
                Text("Close")
            }
        }
    )
}

