package com.ainotebuddy.app.ui.dashboard

import android.annotation.TargetApi
import android.os.Build
import androidx.compose.foundation.background
import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.LazyRow
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.automirrored.filled.Note
import androidx.compose.material.icons.filled.*
import androidx.compose.material.icons.outlined.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.graphics.vector.ImageVector
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextOverflow
import androidx.compose.ui.unit.dp
import com.ainotebuddy.app.data.NoteEntity
import com.ainotebuddy.app.viewmodel.NoteViewModel
import java.text.SimpleDateFormat
import java.util.*
import androidx.compose.foundation.Canvas
import androidx.compose.foundation.shape.CircleShape
import androidx.compose.ui.graphics.Brush
import androidx.compose.ui.geometry.Offset
import androidx.compose.ui.draw.blur
import androidx.compose.animation.core.*
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.foundation.BorderStroke
import androidx.compose.ui.platform.LocalContext
import androidx.biometric.BiometricPrompt
import androidx.core.content.ContextCompat
import com.ainotebuddy.app.ui.components.BannerAdView

// Stub functions for missing functionality
fun toggleFavorite(noteId: Long, viewModel: NoteViewModel) {
    viewModel.toggleFavorite(noteId)
}

fun togglePin(noteId: Long, viewModel: NoteViewModel) {
    viewModel.togglePin(noteId)
}

@Composable
fun DashboardScreen(
    viewModel: NoteViewModel,
    onNoteClick: (NoteEntity) -> Unit,
    onNewNote: () -> Unit,
    onSearch: () -> Unit,
    onCategoryClick: (String) -> Unit,
    onTagClick: (String) -> Unit,
    onTemplates: () -> Unit = {},
    onCategories: () -> Unit = {}
) {
    val notes by viewModel.notes.collectAsState()
    val favoriteNotes by viewModel.favoriteNotes.collectAsState()
    val pinnedNotes by viewModel.pinnedNotes.collectAsState()
    val vaultNotes by viewModel.vaultNotes.collectAsState()
    val context = LocalContext.current
    var vaultUnlocked by remember { mutableStateOf(false) }
    
    val themeState = remember { mutableStateOf("light") }
    val isDarkTheme = themeState.value == "dark"
    
    Box(modifier = Modifier.fillMaxSize()) {
        // Animated gradient background
        AnimatedGradientBackground()
        
        LazyColumn(
            modifier = Modifier
                .fillMaxSize()
                .padding(bottom = 80.dp),
            contentPadding = PaddingValues(12.dp),
            verticalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            // Header Section
            item {
                DashboardHeader(
                    onSearch = onSearch,
                    onNewNote = onNewNote
                )
            }
            
            // Quick Stats Cards
            item {
                QuickStatsSection(notes = notes)
            }
            
            // Quick Actions
            item {
                QuickActionsSection(
                    onNewNote = onNewNote,
                    onSearch = onSearch,
                    onTemplates = onTemplates,
                    onCategories = onCategories
                )
            }
            
            // Pinned Notes
            if (pinnedNotes.isNotEmpty()) {
                item {
                    SectionHeader(
                        title = "📌 Pinned Notes",
                        subtitle = "${pinnedNotes.size} important notes",
                        icon = Icons.Filled.Star,
                        color = Color(0xFFFFD700)
                    )
                }
                item {
                    PinnedNotesGrid(
                        notes = pinnedNotes,
                        onNoteClick = onNoteClick,
                        viewModel = viewModel
                    )
                }
            }
            
            // Recent Notes
            item {
                SectionHeader(
                    title = "📝 Recent Notes",
                    subtitle = "Your latest thoughts",
                    icon = Icons.AutoMirrored.Filled.Note,
                    color = Color(0xFF6A82FB)
                )
            }
            items(notes.take(6).ifEmpty { listOf() }) { note ->
                PremiumNoteCard(
                    note = note,
                    onClick = { onNoteClick(note) },
                    onFavorite = { viewModel.toggleFavorite(note.id) },
                    onPin = { viewModel.togglePin(note.id) },
                    onMoveToVault = { viewModel.moveNoteToVault(note.id) }
                )
            }
            
            // Vault Section
            item {
                VaultSection(
                    vaultNotes = vaultNotes,
                    isUnlocked = vaultUnlocked,
                    onUnlock = {
                        authenticateVault(context) { vaultUnlocked = true }
                    },
                    onNoteClick = onNoteClick,
                    viewModel = viewModel
                )
            }
            
            // Categories Overview
            item {
                CategoriesOverviewSection(
                    notes = notes,
                    onCategoryClick = onCategoryClick
                )
            }
            
            // Banner Ad
            item {
                BannerAdView(
                    modifier = Modifier.padding(vertical = 8.dp)
                )
            }
        }
    }
}

@Composable
fun AnimatedGradientBackground() {
    val infiniteTransition = rememberInfiniteTransition(label = "gradient")
    val offset by infiniteTransition.animateFloat(
        initialValue = 0f,
        targetValue = 1000f,
        animationSpec = infiniteRepeatable(
            animation = tween(12000, easing = LinearEasing),
            repeatMode = RepeatMode.Reverse
        ),
        label = "offset"
    )
    
    Canvas(modifier = Modifier.fillMaxSize()) {
        drawRect(
            brush = Brush.linearGradient(
                colors = listOf(
                    Color(0xFF1a1a2e).copy(alpha = 0.9f),
                    Color(0xFF16213e).copy(alpha = 0.9f),
                    Color(0xFF0f3460).copy(alpha = 0.9f),
                    Color(0xFF533483).copy(alpha = 0.9f)
                ),
                start = Offset(0f, offset * 0.5f),
                end = Offset(offset * 0.5f, size.height)
            )
        )
    }
}

@Composable
fun DashboardHeader(
    onSearch: () -> Unit,
    onNewNote: () -> Unit
) {
    GlassCard(
        modifier = Modifier.fillMaxWidth()
    ) {
        Column {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Column {
                    Text(
                        text = "Welcome back! 👋",
                        style = MaterialTheme.typography.headlineMedium,
                        fontWeight = FontWeight.Bold,
                        color = Color.White
                    )
                    Text(
                        text = "Ready to capture your thoughts?",
                        style = MaterialTheme.typography.bodyMedium,
                        color = Color.White.copy(alpha = 0.8f)
                    )
                }
                
                IconButton(
                    onClick = onSearch,
                    modifier = Modifier
                        .size(40.dp)
                        .background(Color.White.copy(alpha = 0.2f), CircleShape)
                ) {
                    Icon(
                        Icons.Filled.Search,
                        contentDescription = "Search",
                        tint = Color.White
                    )
                }
            }
            
            Spacer(modifier = Modifier.height(12.dp))
            
            Button(
                onClick = onNewNote,
                modifier = Modifier.fillMaxWidth(),
                colors = ButtonDefaults.buttonColors(
                    containerColor = Color.White,
                    contentColor = Color(0xFF667eea)
                ),
                shape = RoundedCornerShape(12.dp)
            ) {
                Icon(Icons.Filled.Add, contentDescription = null)
                Spacer(modifier = Modifier.width(8.dp))
                Text("Create New Note", fontWeight = FontWeight.Bold)
            }
        }
    }
}

@Composable
fun QuickStatsSection(notes: List<NoteEntity>) {
    LazyRow(
        horizontalArrangement = Arrangement.spacedBy(12.dp)
    ) {
        item {
            StatCard(
                title = "Total Notes",
                value = notes.size.toString(),
                icon = Icons.Filled.Note,
                color = Color(0xFF6A82FB)
            )
        }
        item {
            StatCard(
                title = "Favorites",
                value = notes.count { it.isFavorite }.toString(),
                icon = Icons.Filled.Favorite,
                color = Color(0xFFFC5C7D)
            )
        }
        item {
            StatCard(
                title = "Pinned",
                value = notes.count { it.isPinned }.toString(),
                icon = Icons.Filled.Star,
                color = Color(0xFFFFD700)
            )
        }
        item {
            StatCard(
                title = "In Vault",
                value = notes.count { it.isInVault }.toString(),
                icon = Icons.Filled.Lock,
                color = Color(0xFF00FFC6)
            )
        }
    }
}

@Composable
fun StatCard(
    title: String,
    value: String,
    icon: ImageVector,
    color: Color
) {
    GlassCard(
        modifier = Modifier.width(120.dp)
    ) {
        Column(
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Icon(
                icon,
                contentDescription = null,
                tint = color,
                modifier = Modifier.size(24.dp)
            )
            Spacer(modifier = Modifier.height(8.dp))
            Text(
                text = value,
                style = MaterialTheme.typography.headlineSmall,
                fontWeight = FontWeight.Bold,
                color = Color.White
            )
            Text(
                text = title,
                style = MaterialTheme.typography.bodySmall,
                color = Color.White.copy(alpha = 0.8f),
                textAlign = TextAlign.Center
            )
        }
    }
}

@Composable
fun QuickActionsSection(
    onNewNote: () -> Unit,
    onSearch: () -> Unit,
    onTemplates: () -> Unit,
    onCategories: () -> Unit
) {
    GlassCard {
        Column {
            Text(
                text = "Quick Actions",
                style = MaterialTheme.typography.titleLarge,
                fontWeight = FontWeight.Bold,
                color = Color.White
            )
            Spacer(modifier = Modifier.height(16.dp))
            
            Column(
                verticalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    ActionCard(
                        title = "New Note",
                        icon = Icons.Filled.Add,
                        color = Color(0xFF6A82FB),
                        onClick = onNewNote,
                        modifier = Modifier.weight(1f)
                    )
                    ActionCard(
                        title = "Search",
                        icon = Icons.Filled.Search,
                        color = Color(0xFFFC5C7D),
                        onClick = onSearch,
                        modifier = Modifier.weight(1f)
                    )
                }
                Row(
                    modifier = Modifier.fillMaxWidth(),
                    horizontalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    ActionCard(
                        title = "Templates",
                        icon = Icons.Filled.Description,
                        color = Color(0xFF00FFC6),
                        onClick = onTemplates,
                        modifier = Modifier.weight(1f)
                    )
                    ActionCard(
                        title = "Categories",
                        icon = Icons.Filled.Folder,
                        color = Color(0xFFFFD700),
                        onClick = onCategories,
                        modifier = Modifier.weight(1f)
                    )
                }
            }
        }
    }
}

@Composable
fun ActionCard(
    title: String,
    icon: ImageVector,
    color: Color,
    onClick: () -> Unit,
    modifier: Modifier = Modifier
) {
    Card(
        onClick = onClick,
        modifier = modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(
            containerColor = Color.White.copy(alpha = 0.1f)
        ),
        shape = RoundedCornerShape(12.dp),
        border = BorderStroke(1.dp, Color.White.copy(alpha = 0.2f))
    ) {
        Column(
            modifier = Modifier.padding(16.dp),
