package com.ainotebuddy.app.settings

import android.content.Context
import androidx.compose.runtime.Composable
import androidx.compose.ui.platform.LocalContext
import com.ainotebuddy.app.repository.AdvancedNoteRepository

/**
 * Test wrapper for SettingsScreen to help debug crashes
 */
@Composable
fun SafeSettingsScreen(
    repository: AdvancedNoteRepository,
    backupManager: BackupRestoreManager,
    onSignOut: () -> Unit = {},
    onSyncToDrive: () -> Unit = {},
    onSyncFromDrive: () -> Unit = {},
    onAISettingsClick: () -> Unit = {}
) {
    try {
        SettingsScreen(
            repository = repository,
            backupManager = backupManager,
            onSignOut = onSignOut,
            onSyncToDrive = onSyncToDrive,
            onSyncFromDrive = onSyncFromDrive,
            onAISettingsClick = onAISettingsClick
        )
    } catch (e: Exception) {
        // Log the error for debugging
        android.util.Log.e("SettingsScreen", "Crash in settings screen", e)
        
        // Show a simple error message
        androidx.compose.material3.Text(
            text = "Settings temporarily unavailable. Error: ${e.message}",
            color = androidx.compose.ui.graphics.Color.Red
        )
    }
}

