@Composable
fun PinnedNotesGrid(
    notes: List<NoteEntity>,
    onNoteClick: (NoteEntity) -> Unit,
    viewModel: NoteViewModel
) {
    // Use regular Column and Row instead of LazyVerticalGrid to avoid nesting scrollable components
    Column(
        verticalArrangement = Arrangement.spacedBy(12.dp)
    ) {
        notes.chunked(2).forEach { rowNotes ->
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                rowNotes.forEach { note ->
                    Box(modifier = Modifier.weight(1f)) {
                        PremiumNoteCard(
                            note = note,
                            onClick = { onNoteClick(note) },
                            onFavorite = { toggleFavorite(note.id, viewModel) },
                            onPin = { togglePin(note.id, viewModel) },
                            onMoveToVault = { viewModel.moveNoteToVault(note.id) },
                            isCompact = true
                        )
                    }
                }
                // Fill remaining space if odd number of notes in row
                if (rowNotes.size == 1) {
                    Spacer(modifier = Modifier.weight(1f))
                }
            }
        }
    }
}

@Composable
fun PremiumNoteCard(
    note: NoteEntity,
    onClick: () -> Unit,
    onFavorite: () -> Unit,
    onPin: () -> Unit,
    onMoveToVault: () -> Unit,
    isCompact: Boolean = false
) {
    Card(
        onClick = onClick,
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(
            containerColor = Color.White.copy(alpha = 0.1f)
        ),
        shape = RoundedCornerShape(16.dp),
        border = BorderStroke(1.dp, Color.White.copy(alpha = 0.2f))
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.Top
            ) {
                Text(
                    text = note.title,
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold,
                    color = Color.White,
                    maxLines = 2,
                    overflow = TextOverflow.Ellipsis,
                    modifier = Modifier.weight(1f)
                )
                
                Row {
                    if (note.isFavorite) {
                        Icon(
                            Icons.Filled.Favorite,
                            contentDescription = null,
                            tint = Color(0xFFFC5C7D),
                            modifier = Modifier.size(16.dp)
                        )
                    }
                    if (note.isPinned) {
                        Icon(
                            Icons.Filled.Star,
                            contentDescription = null,
                            tint = Color(0xFFFFD700),
                            modifier = Modifier.size(16.dp)
                        )
                    }
                }
            }
            
            if (!isCompact) {
                Spacer(modifier = Modifier.height(8.dp))
                Text(
                    text = note.content,
                    style = MaterialTheme.typography.bodyMedium,
                    color = Color.White.copy(alpha = 0.8f),
                    maxLines = 3,
                    overflow = TextOverflow.Ellipsis
                )
            }
            
            Spacer(modifier = Modifier.height(8.dp))
            
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = formatDate(note.updatedAt),
                    style = MaterialTheme.typography.bodySmall,
                    color = Color.White.copy(alpha = 0.6f)
                )
                
                Row(
                    horizontalArrangement = Arrangement.spacedBy(4.dp)
                ) {
                    IconButton(
                        onClick = onFavorite,
                        modifier = Modifier.size(24.dp)
                    ) {
                        Icon(
                            if (note.isFavorite) Icons.Filled.Favorite else Icons.Outlined.FavoriteBorder,
                            contentDescription = "Favorite",
                            tint = if (note.isFavorite) Color(0xFFFC5C7D) else Color.White.copy(alpha = 0.6f),
                            modifier = Modifier.size(16.dp)
                        )
                    }
                    IconButton(
                        onClick = onPin,
                        modifier = Modifier.size(24.dp)
                    ) {
                        Icon(
                            if (note.isPinned) Icons.Filled.Star else Icons.Outlined.StarBorder,
                            contentDescription = "Pin",
                            tint = if (note.isPinned) Color(0xFFFFD700) else Color.White.copy(alpha = 0.6f),
                            modifier = Modifier.size(16.dp)
                        )
                    }
                }
            }
        }
    }
}

@TargetApi(Build.VERSION_CODES.N)
@Composable
fun VaultSection(
    vaultNotes: List<NoteEntity>,
    isUnlocked: Boolean,
    onUnlock: () -> Unit,
    onNoteClick: (NoteEntity) -> Unit,
    viewModel: NoteViewModel
) {
    GlassCard {
        Column {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Row(
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    Icon(
                        Icons.Filled.Lock,
                        contentDescription = null,
                        tint = Color(0xFF00FFC6),
                        modifier = Modifier.size(24.dp)
                    )
                    Spacer(modifier = Modifier.width(12.dp))
                    Column {
                        Text(
                            text = "Secure Vault",
                            style = MaterialTheme.typography.titleLarge,
                            fontWeight = FontWeight.Bold,
                            color = Color.White
                        )
                        Text(
                            text = "${vaultNotes.size} secure notes",
                            style = MaterialTheme.typography.bodyMedium,
                            color = Color.White.copy(alpha = 0.7f)
                        )
                    }
                }
                
                if (!isUnlocked) {
                    Button(
                        onClick = onUnlock,
                        colors = ButtonDefaults.buttonColors(
                            containerColor = Color(0xFF00FFC6),
                            contentColor = Color.Black
                        ),
                        shape = RoundedCornerShape(8.dp)
                    ) {
                        Text("Unlock", fontWeight = FontWeight.Bold)
                    }
                }
            }
            
            if (isUnlocked && vaultNotes.isNotEmpty()) {
                Spacer(modifier = Modifier.height(16.dp))
                vaultNotes.forEach { note ->
                    PremiumNoteCard(
                        note = note,
                        onClick = { onNoteClick(note) },
                        onFavorite = { toggleFavorite(note.id, viewModel) },
                        onPin = { togglePin(note.id, viewModel) },
                        onMoveToVault = { viewModel.moveNoteToVault(note.id) },
                        isCompact = true
                    )
                    Spacer(modifier = Modifier.height(8.dp))
                }
            }
        }
    }
}

@Composable
fun CategoriesOverviewSection(
    notes: List<NoteEntity>,
    onCategoryClick: (String) -> Unit
) {
    val categories = notes.map { it.category }.distinct().filter { it.isNotBlank() }
    
    if (categories.isNotEmpty()) {
        GlassCard {
            Column {
                Text(
                    text = "Categories",
                    style = MaterialTheme.typography.titleLarge,
                    fontWeight = FontWeight.Bold,
                    color = Color.White
                )
                Spacer(modifier = Modifier.height(16.dp))
                
                // Use regular Column and Row instead of LazyVerticalGrid to avoid nesting scrollable components
                Column(
                    verticalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    categories.chunked(2).forEach { rowCategories ->
                        Row(
                            modifier = Modifier.fillMaxWidth(),
                            horizontalArrangement = Arrangement.spacedBy(12.dp)
                        ) {
                            rowCategories.forEach { category ->
                                Box(modifier = Modifier.weight(1f)) {
                                    val noteCount = notes.count { it.category == category }
                                    CategoryCard(
                                        name = category,
                                        count = noteCount,
                                        onClick = { onCategoryClick(category) }
                                    )
                                }
                            }
                            // Fill remaining space if odd number of categories in row
                            if (rowCategories.size == 1) {
                                Spacer(modifier = Modifier.weight(1f))
                            }
                        }
                    }
                }
            }
        }
    }
}

