@Composable
fun DashboardScreen(
    viewModel: NoteViewModel,
    onNoteClick: (NoteEntity) -> Unit,
    onNewNote: () -> Unit,
    onSearch: () -> Unit,
    onCategoryClick: (String) -> Unit,
    onTagClick: (String) -> Unit,
    onTemplates: () -> Unit = {},
    onCategories: () -> Unit = {}
) {
    val notes by viewModel.notes.collectAsState()
    val favoriteNotes by viewModel.favoriteNotes.collectAsState()
    val pinnedNotes by viewModel.pinnedNotes.collectAsState()
    val vaultNotes by viewModel.vaultNotes.collectAsState()
    val context = LocalContext.current
    var vaultUnlocked by remember { mutableStateOf(false) }
    
    val themeState = remember { mutableStateOf("light") }
    val isDarkTheme = themeState.value == "dark"
    
    Box(modifier = Modifier.fillMaxSize()) {
        // Animated gradient background
        AnimatedGradientBackground()
        
        LazyColumn(
            modifier = Modifier
                .fillMaxSize()
                .padding(bottom = 80.dp),
            contentPadding = PaddingValues(12.dp),
            verticalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            // Header Section
            item {
                DashboardHeader(
                    onSearch = onSearch,
                    onNewNote = onNewNote
                )
            }
            
            // Quick Stats Cards
            item {
                QuickStatsSection(notes = notes)
            }
            
            // Quick Actions
            item {
                QuickActionsSection(
                    onNewNote = onNewNote,
                    onSearch = onSearch,
                    onTemplates = onTemplates,
                    onCategories = onCategories
                )
            }
            
            // Pinned Notes
            if (pinnedNotes.isNotEmpty()) {
                item {
                    SectionHeader(
                        title = "ðŸ“Œ Pinned Notes",
                        subtitle = "${pinnedNotes.size} important notes",
                        icon = Icons.Filled.Star,
                        color = Color(0xFFFFD700)
                    )
                }
                item {
                    PinnedNotesGrid(
                        notes = pinnedNotes,
                        onNoteClick = onNoteClick,
                        viewModel = viewModel
                    )
                }
            }
            
            // Recent Notes
            item {
                SectionHeader(
                    title = "ðŸ“ Recent Notes",
                    subtitle = "Your latest thoughts",
                    icon = Icons.AutoMirrored.Filled.Note,
                    color = Color(0xFF6A82FB)
                )
            }
            items(notes.take(6).ifEmpty { listOf() }) { note ->
                PremiumNoteCard(
                    note = note,
                    onClick = { onNoteClick(note) },
                    onFavorite = { viewModel.toggleFavorite(note.id) },
                    onPin = { viewModel.togglePin(note.id) },
                    onMoveToVault = { viewModel.moveNoteToVault(note.id) }
                )
            }
            
            // Vault Section
            item {
                VaultSection(
                    vaultNotes = vaultNotes,
                    isUnlocked = vaultUnlocked,
                    onUnlock = {
                        authenticateVault(context) { vaultUnlocked = true }
                    },
                    onNoteClick = onNoteClick,
                    viewModel = viewModel
                )
            }
            
            // Categories Overview
            item {
                CategoriesOverviewSection(
                    notes = notes,
                    onCategoryClick = onCategoryClick
                )
            }
            
            // Banner Ad
            item {
                BannerAdView(
                    modifier = Modifier.padding(vertical = 8.dp)
                )
            }
        }
    }
}

@Composable
fun AnimatedGradientBackground() {
    val infiniteTransition = rememberInfiniteTransition(label = "gradient")
    val offset by infiniteTransition.animateFloat(
        initialValue = 0f,
        targetValue = 1000f,
        animationSpec = infiniteRepeatable(
            animation = tween(12000, easing = LinearEasing),
            repeatMode = RepeatMode.Reverse
        ),
        label = "offset"
    )
    
    Canvas(modifier = Modifier.fillMaxSize()) {
        drawRect(
            brush = Brush.linearGradient(
                colors = listOf(
                    Color(0xFF1a1a2e).copy(alpha = 0.9f),
                    Color(0xFF16213e).copy(alpha = 0.9f),
                    Color(0xFF0f3460).copy(alpha = 0.9f),
                    Color(0xFF533483).copy(alpha = 0.9f)
                ),
                start = Offset(0f, offset * 0.5f),
                end = Offset(offset * 0.5f, size.height)
            )
        )
    }
}

