class MainActivity : ComponentActivity() {

    private val signInLauncher = registerForActivityResult(
        ActivityResultContracts.StartActivityForResult()
    ) { result ->
        val task = com.google.android.gms.auth.api.signin.GoogleSignIn.getSignedInAccountFromIntent(result.data)
        try {
            val account = task.getResult(ApiException::class.java)
            Toast.makeText(this, "Welcome, ${account.displayName}!", Toast.LENGTH_SHORT).show()
            // Refresh the activity to update the signed-in state
            recreate()
        } catch (e: ApiException) {
            val message = when (e.statusCode) {
                12501 -> "Sign in was cancelled"
                12500 -> "Sign in failed. Please try again."
                10 -> "Developer error: Check SHA1 fingerprint and OAuth configuration"
                7 -> "Network error: Check your internet connection"
                8 -> "Internal error: Please try again later"
                else -> "Sign in failed (Code: ${e.statusCode}): ${e.message}"
            }
            Toast.makeText(this, message, Toast.LENGTH_LONG).show()
        }
    }

    private val viewModel by viewModels<NoteViewModel> {
        object : ViewModelProvider.Factory {
            @Suppress("UNCHECKED_CAST")
            override fun <T : ViewModel> create(modelClass: Class<T>): T {
                if (modelClass.isAssignableFrom(NoteViewModel::class.java)) {
                    return NoteViewModel(
                        repository = null, // Using advancedRepository instead
                        advancedRepository = (application as AINoteBuddyApplication).advancedNoteRepository,
                        googleAuthService = GoogleAuthService(this@MainActivity)
                    ) as T
                }
                throw IllegalArgumentException("Unknown ViewModel class")
            }
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // Initialize AdMob
        AdManager.getInstance(this).initialize { success ->
            if (success) {
                android.util.Log.d("MainActivity", "AdMob initialized successfully")
            } else {
                android.util.Log.e("MainActivity", "AdMob initialization failed")
            }
        }

        val prefs = getSharedPreferences("ainotebuddy_prefs", MODE_PRIVATE)
        val firstRun = prefs.getBoolean("first_run", true)
        val googleAuthService = GoogleAuthService(this)
        var isSignedIn = googleAuthService.isSignedIn()

        setContent {
            var showOnboarding by remember { mutableStateOf(firstRun) }
            var showPrivacy by remember { mutableStateOf(false) }
            var currentScreen by remember { mutableStateOf(Screen.DASHBOARD) }
            var signedIn by remember { mutableStateOf(isSignedIn) }

            // Dashboard sub-screen states
            var showTemplates by remember { mutableStateOf(false) }
            var showCategories by remember { mutableStateOf(false) }

            if (showOnboarding) {
                if (showPrivacy) {
                    PrivacyPolicyScreen(onBack = { showPrivacy = false })
                } else {
                    OnboardingScreen(
                        onFinish = {
                            prefs.edit().putBoolean("first_run", false).apply()
                            showOnboarding = false
                        },
                        onPrivacy = { showPrivacy = true }
                    )
                }
            } else {
                if (!signedIn) {
                    // Show integrated login screen
                    LoginScreen(
                        onSignInClick = {
                            val googleAuthService = GoogleAuthService(this@MainActivity)
                            val signInIntent = googleAuthService.getSignInIntent()
                            signInLauncher.launch(signInIntent)
                        },
                        onSkip = {
                            signedIn = true // Allow user to continue without signing in
                        }
                    )
                } else {
                    AINoteBuddyTheme(themeState = ThemeManager.themeState) {
                        Surface(color = MaterialTheme.colorScheme.background) {
                            if (showTemplates) {
                                TemplatesScreen(
                                    onBack = { showTemplates = false },
                                    onTemplateSelect = { title, content ->
                                        createNoteFromTemplate(title, content)
                                        showTemplates = false
                                    }
                                )
                            } else if (showCategories) {
                                CategoriesScreen(
                                    viewModel = viewModel,
                                    onBack = { showCategories = false },
                                    onCategoryClick = { category ->
                                        // Filter notes by category and go to notes screen
                                        showCategories = false
                                        currentScreen = Screen.NOTES
                                    }
                                )
                            } else {
                                MainAppContent(
                                    currentScreen = currentScreen,
                                    onScreenChange = { currentScreen = it },
                                    viewModel = viewModel,
                                    onSignOut = {
                                        signOut()
                                        signedIn = false
                                    },
                                    onSyncToDrive = { syncToDrive() },
                                    onSyncFromDrive = { syncFromDrive() },
                                    onNoteClick = { note -> openNoteEditor(note) },
                                    onNewNote = { openNoteEditor(null) },
                                    onShowTemplates = { showTemplates = true },
                                    onShowCategories = { showCategories = true }
                                )
                            }
                        }
                    }
                }
            }
        }
    }

    private fun openNoteEditor(note: NoteEntity?) {
        // Open note editor directly without ads
        launchNoteEditor(note)
    }

    private fun launchNoteEditor(note: NoteEntity?) {
        val intent = Intent(this, NoteEditorActivity::class.java).apply {
            note?.let { putExtra("note_id", it.id) }
        }
        startActivity(intent)
    }

    private fun createNoteFromTemplate(title: String, content: String) {
        val intent = Intent(this, NoteEditorActivity::class.java).apply {
            putExtra("template_title", title)
            putExtra("template_content", content)
        }
        startActivity(intent)
    }

    override fun onResume() {
        super.onResume()
        // Show app open ad when returning to app
        val adManager = AdManager.getInstance(this)
        if (adManager.isInitialized.value) {
            adManager.appOpenAdManager.showAdIfAvailable(this)
        }
    }

    private fun signOut() {
        val googleAuthService = GoogleAuthService(this)
        lifecycleScope.launch {
            googleAuthService.signOut()
            startActivity(Intent(this@MainActivity, LoginActivity::class.java))
            finish()
        }
    }

    private fun syncToDrive() {
        val googleAuthService = GoogleAuthService(this)
        val syncService = (application as AINoteBuddyApplication).googleDriveSyncService

        val account = googleAuthService.getCurrentAccount()
        if (account != null) {
            Toast.makeText(this, "Syncing to Google Drive...", Toast.LENGTH_SHORT).show()
            viewModel.syncToDrive(account, syncService)
        } else {
            Toast.makeText(this, "Please sign in to sync", Toast.LENGTH_SHORT).show()
        }
    }

    private fun syncFromDrive() {
        val googleAuthService = GoogleAuthService(this)
        val syncService = (application as AINoteBuddyApplication).googleDriveSyncService

        val account = googleAuthService.getCurrentAccount()
        if (account != null) {
            Toast.makeText(this, "Syncing from Google Drive...", Toast.LENGTH_SHORT).show()
            viewModel.syncFromDrive(account, syncService)
        } else {
            Toast.makeText(this, "Please sign in to sync", Toast.LENGTH_SHORT).show()
        }
    }
}

@Composable
fun LoadingScreen(modifier: Modifier = Modifier) {
    Box(
        modifier = modifier.fillMaxSize(),
        contentAlignment = Alignment.Center
    ) {
        Column(
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            CircularProgressIndicator(
                modifier = Modifier.size(50.dp),
                color = MaterialTheme.colorScheme.primary
            )
            Spacer(modifier = Modifier.height(16.dp))
            AnimatedVisibility(
                visible = true,
                enter = fadeIn() + slideInVertically(),
                exit = fadeOut() + slideOutVertically()
            ) {
                Text(
                    text = "Loading...",
                    style = MaterialTheme.typography.bodyLarge,
                    color = MaterialTheme.colorScheme.primary
                )
            }
        }
    }
}

@Composable
fun ErrorScreen(
    message: String,
    onRetry: () -> Unit,
    modifier: Modifier = Modifier
) {
    Column(
        modifier = modifier.fillMaxSize().padding(32.dp),
        verticalArrangement = Arrangement.Center,
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        Icon(
            imageVector = Icons.Filled.Error,
            contentDescription = null,
            modifier = Modifier.size(64.dp),
            tint = MaterialTheme.colorScheme.error
        )
        Spacer(modifier = Modifier.height(16.dp))
        Text(
            text = message,
            style = MaterialTheme.typography.bodyLarge,
            textAlign = TextAlign.Center,
            color = MaterialTheme.colorScheme.error
        )
        Spacer(modifier = Modifier.height(24.dp))
        Button(
            onClick = onRetry,
            colors = ButtonDefaults.buttonColors(
                containerColor = MaterialTheme.colorScheme.errorContainer,
                contentColor = MaterialTheme.colorScheme.onErrorContainer
            )
        ) {
            Icon(
                Icons.Filled.Refresh,
                contentDescription = null,
                modifier = Modifier.size(18.dp)
            )
            Spacer(modifier = Modifier.width(8.dp))
            Text("Retry")
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MainAppContent(
    currentScreen: Screen,
    onScreenChange: (Screen) -> Unit,
    viewModel: NoteViewModel,
    onSignOut: () -> Unit,
    onSyncToDrive: () -> Unit,
    onSyncFromDrive: () -> Unit,
    onNoteClick: (NoteEntity) -> Unit,
    onNewNote: () -> Unit,
    onShowTemplates: () -> Unit = {},
    onShowCategories: () -> Unit = {},
    modifier: Modifier = Modifier
) {
    var isLoading by remember { mutableStateOf(false) }
    var error by remember { mutableStateOf<String?>(null) }
    var isRefreshing by remember { mutableStateOf(false) }

    // Handle back navigation
    BackHandler(enabled = currentScreen != Screen.DASHBOARD) {
        onScreenChange(Screen.DASHBOARD)
    }

    // Error handling
    error?.let { errorMessage ->
        ErrorScreen(
            message = errorMessage,
            onRetry = { error = null }
        )
        return
    }

    // Loading state
    if (isLoading) {
        LoadingScreen()
        return
    }

    val context = LocalContext.current
    val scope = rememberCoroutineScope()

    @OptIn(ExperimentalMaterial3Api::class)
    val floatingActionButton: @Composable () -> Unit = {
        FloatingActionButton(
            onClick = onNewNote,
            containerColor = MaterialTheme.colorScheme.primary,
            contentColor = MaterialTheme.colorScheme.onPrimary
        ) {
            Icon(Icons.Filled.Add, contentDescription = "Add new note")
        }
    }

    Scaffold(
        floatingActionButton = {
            if (currentScreen == Screen.DASHBOARD || currentScreen == Screen.NOTES) {
                FloatingActionButton(
                    onClick = onNewNote,
                    containerColor = MaterialTheme.colorScheme.primary,
                    contentColor = MaterialTheme.colorScheme.onPrimary
                ) {
                    Icon(Icons.Filled.Add, contentDescription = "Add Note")
                }
            }
        },
        bottomBar = {
            @OptIn(ExperimentalMaterial3Api::class)
            NavigationBar {
                NavigationBarItem(
                    icon = { Icon(Icons.Filled.Dashboard, "Dashboard") },
                    label = { Text("Dashboard") },
                    selected = currentScreen == Screen.DASHBOARD,
                    onClick = { onScreenChange(Screen.DASHBOARD) }
                )
                NavigationBarItem(
                    icon = { Icon(Icons.AutoMirrored.Filled.Note, "Notes") },
                    label = { Text("Notes") },
                    selected = currentScreen == Screen.NOTES,
                    onClick = { onScreenChange(Screen.NOTES) }
                )
                NavigationBarItem(
                    icon = { Icon(Icons.Filled.Settings, "Settings") },
                    label = { Text("Settings") },
                    selected = currentScreen == Screen.SETTINGS,
                    onClick = { onScreenChange(Screen.SETTINGS) }
                )
            }
        }
    ) { paddingValues ->
        Box(modifier = Modifier
            .fillMaxSize()
            .padding(paddingValues)) {
            AnimatedContent(
                targetState = currentScreen,
                transitionSpec = {
                    when {
                        targetState == Screen.DASHBOARD -> {
                            (slideInHorizontally { width -> -width } + fadeIn()) togetherWith
                            (slideOutHorizontally { width -> width } + fadeOut())
                        }
                        initialState == Screen.DASHBOARD -> {
                            (slideInHorizontally { width -> width } + fadeIn()) togetherWith
                            (slideOutHorizontally { width -> -width } + fadeOut())
                        }
                        else -> {
                            (slideInHorizontally { width -> width } + fadeIn()) togetherWith
                            (slideOutHorizontally { width -> -width } + fadeOut())
                        }
                    }
                },
                label = "screen_transition"
            ) { screen ->
                when (screen) {
                Screen.DASHBOARD -> {
                    DashboardScreen(
                        viewModel = viewModel,
                        onNoteClick = onNoteClick,
                        onNewNote = onNewNote,
                        onSearch = { /* Navigate to search */ },
                        onCategoryClick = { category -> 
                            // Filter notes by category
                        },
                        onTagClick = { tag -> 
                            // Filter notes by tag
                        },
                        onTemplates = onShowTemplates,
                        onCategories = onShowCategories,
                        onShowAllNotes = { onScreenChange(Screen.NOTES) },
                        onShowFavorites = { onScreenChange(Screen.NOTES) },
                        onShowPinned = { onScreenChange(Screen.NOTES) },
                        onShowVault = { onScreenChange(Screen.NOTES) }
                    )
                }
                Screen.NOTES -> {
                    SavedNotesListScreen(
                        viewModel = viewModel,
                        onNoteClick = onNoteClick,
                        onNewNote = onNewNote
                    )
                }
                Screen.SETTINGS -> {
                    SettingsScreen(
                        repository = (context.applicationContext as AINoteBuddyApplication).advancedNoteRepository,
                        backupManager = BackupRestoreManager(
                            context = context,
                            repository = (context.applicationContext as AINoteBuddyApplication).advancedNoteRepository
                        ),
                        onSignOut = onSignOut,
                        onSyncToDrive = onSyncToDrive,
                        onSyncFromDrive = onSyncFromDrive,
                        onAISettingsClick = { onScreenChange(Screen.AI_SETTINGS) }
                    )
                }
                Screen.AI_SETTINGS -> {
                    AISettingsScreen(
                        onBackClick = { onScreenChange(Screen.SETTINGS) }
                    )
                }
                Screen.CALENDAR -> {
                    CalendarNotesScreen(
                        viewModel = viewModel,
