class MainActivity : ComponentActivity() {

    private val signInLauncher = registerForActivityResult(
        ActivityResultContracts.StartActivityForResult()
    ) { result ->
        val task = com.google.android.gms.auth.api.signin.GoogleSignIn.getSignedInAccountFromIntent(result.data)
        try {
            val account = task.getResult(ApiException::class.java)
            Toast.makeText(this, "Welcome, ${account.displayName}!", Toast.LENGTH_SHORT).show()
            // Refresh the activity to update the signed-in state
            recreate()
        } catch (e: ApiException) {
            val message = when (e.statusCode) {
                12501 -> "Sign in was cancelled"
                12500 -> "Sign in failed. Please try again."
                10 -> "Developer error: Check SHA1 fingerprint and OAuth configuration"
                7 -> "Network error: Check your internet connection"
                8 -> "Internal error: Please try again later"
                else -> "Sign in failed (Code: ${e.statusCode}): ${e.message}"
            }
            Toast.makeText(this, message, Toast.LENGTH_LONG).show()
        }
    }

    private val viewModel by viewModels<NoteViewModel> {
        object : ViewModelProvider.Factory {
            @Suppress("UNCHECKED_CAST")
            override fun <T : ViewModel> create(modelClass: Class<T>): T {
                if (modelClass.isAssignableFrom(NoteViewModel::class.java)) {
                    return NoteViewModel(
                        repository = null, // Using advancedRepository instead
                        advancedRepository = (application as AINoteBuddyApplication).advancedNoteRepository,
                        googleAuthService = GoogleAuthService(this@MainActivity)
                    ) as T
                }
                throw IllegalArgumentException("Unknown ViewModel class")
            }
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

        // Initialize AdMob
        AdManager.getInstance(this).initialize { success ->
            if (success) {
                android.util.Log.d("MainActivity", "AdMob initialized successfully")
            } else {
                android.util.Log.e("MainActivity", "AdMob initialization failed")
            }
        }

        val prefs = getSharedPreferences("ainotebuddy_prefs", MODE_PRIVATE)
        val firstRun = prefs.getBoolean("first_run", true)
        val googleAuthService = GoogleAuthService(this)
        var isSignedIn = googleAuthService.isSignedIn()

        setContent {
            var showOnboarding by remember { mutableStateOf(firstRun) }
            var showPrivacy by remember { mutableStateOf(false) }
            var currentScreen by remember { mutableStateOf(Screen.DASHBOARD) }
            var signedIn by remember { mutableStateOf(isSignedIn) }

            // Dashboard sub-screen states
            var showTemplates by remember { mutableStateOf(false) }
            var showCategories by remember { mutableStateOf(false) }

            if (showOnboarding) {
                if (showPrivacy) {
                    PrivacyPolicyScreen(onBack = { showPrivacy = false })
                } else {
                    OnboardingScreen(
                        onFinish = {
                            prefs.edit().putBoolean("first_run", false).apply()
                            showOnboarding = false
                        },
                        onPrivacy = { showPrivacy = true }
                    )
                }
            } else {
                if (!signedIn) {
                    // Show integrated login screen
                    LoginScreen(
                        onSignInClick = {
                            val googleAuthService = GoogleAuthService(this@MainActivity)
                            val signInIntent = googleAuthService.getSignInIntent()
                            signInLauncher.launch(signInIntent)
                        },
                        onSkip = {
                            signedIn = true // Allow user to continue without signing in
                        }
                    )
                } else {
                    AINoteBuddyTheme(themeState = ThemeManager.themeState) {
                        Surface(color = MaterialTheme.colorScheme.background) {
                            if (showTemplates) {
                                TemplatesScreen(
                                    onBack = { showTemplates = false },
                                    onTemplateSelect = { title, content ->
                                        createNoteFromTemplate(title, content)
                                        showTemplates = false
                                    }
                                )
                            } else if (showCategories) {
                                CategoriesScreen(
                                    viewModel = viewModel,
                                    onBack = { showCategories = false },
                                    onCategoryClick = { category ->
                                        // Filter notes by category and go to notes screen
                                        showCategories = false
                                        currentScreen = Screen.NOTES
                                    }
                                )
                            } else {
                                MainAppContent(
                                    currentScreen = currentScreen,
                                    onScreenChange = { currentScreen = it },
                                    viewModel = viewModel,
                                    onSignOut = {
                                        signOut()
                                        signedIn = false
                                    },
                                    onSyncToDrive = { syncToDrive() },
                                    onSyncFromDrive = { syncFromDrive() },
                                    onNoteClick = { note -> openNoteEditor(note) },
                                    onNewNote = { openNoteEditor(null) },
                                    onShowTemplates = { showTemplates = true },
                                    onShowCategories = { showCategories = true }
                                )
                            }
                        }
                    }
                }
            }
        }
    }

    private fun openNoteEditor(note: NoteEntity?) {
        // Open note editor directly without ads
        launchNoteEditor(note)
    }

    private fun launchNoteEditor(note: NoteEntity?) {
        val intent = Intent(this, NoteEditorActivity::class.java).apply {
            note?.let { putExtra("note_id", it.id) }
        }
        startActivity(intent)
    }

    private fun createNoteFromTemplate(title: String, content: String) {
        val intent = Intent(this, NoteEditorActivity::class.java).apply {
            putExtra("template_title", title)
            putExtra("template_content", content)
        }
        startActivity(intent)
    }

    override fun onResume() {
        super.onResume()
        // Show app open ad when returning to app
        val adManager = AdManager.getInstance(this)
        if (adManager.isInitialized.value) {
            adManager.appOpenAdManager.showAdIfAvailable(this)
        }
    }

    private fun signOut() {
        val googleAuthService = GoogleAuthService(this)
        lifecycleScope.launch {
            googleAuthService.signOut()
            startActivity(Intent(this@MainActivity, LoginActivity::class.java))
            finish()
        }
    }

    private fun syncToDrive() {
        val googleAuthService = GoogleAuthService(this)
        val syncService = (application as AINoteBuddyApplication).googleDriveSyncService

        val account = googleAuthService.getCurrentAccount()
        if (account != null) {
            Toast.makeText(this, "Syncing to Google Drive...", Toast.LENGTH_SHORT).show()
            viewModel.syncToDrive(account, syncService)
        } else {
            Toast.makeText(this, "Please sign in to sync", Toast.LENGTH_SHORT).show()
        }
    }

    private fun syncFromDrive() {
        val googleAuthService = GoogleAuthService(this)
        val syncService = (application as AINoteBuddyApplication).googleDriveSyncService

        val account = googleAuthService.getCurrentAccount()
        if (account != null) {
            Toast.makeText(this, "Syncing from Google Drive...", Toast.LENGTH_SHORT).show()
            viewModel.syncFromDrive(account, syncService)
        } else {
            Toast.makeText(this, "Please sign in to sync", Toast.LENGTH_SHORT).show()
        }
    }
}

