@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MainAppContent(
    currentScreen: Screen,
    onScreenChange: (Screen) -> Unit,
    viewModel: NoteViewModel,
    onSignOut: () -> Unit,
    onSyncToDrive: () -> Unit,
    onSyncFromDrive: () -> Unit,
    onNoteClick: (NoteEntity) -> Unit,
    onNewNote: () -> Unit,
    onShowTemplates: () -> Unit = {},
    onShowCategories: () -> Unit = {},
    modifier: Modifier = Modifier
) {
    var isLoading by remember { mutableStateOf(false) }
    var error by remember { mutableStateOf<String?>(null) }
    var isRefreshing by remember { mutableStateOf(false) }

    // Handle back navigation
    BackHandler(enabled = currentScreen != Screen.DASHBOARD) {
        onScreenChange(Screen.DASHBOARD)
    }

    // Error handling
    error?.let { errorMessage ->
        ErrorScreen(
            message = errorMessage,
            onRetry = { error = null }
        )
        return
    }

    // Loading state
    if (isLoading) {
        LoadingScreen()
        return
    }

    val context = LocalContext.current
    val scope = rememberCoroutineScope()

    @OptIn(ExperimentalMaterial3Api::class)
    val floatingActionButton: @Composable () -> Unit = {
        FloatingActionButton(
            onClick = onNewNote,
            containerColor = MaterialTheme.colorScheme.primary,
            contentColor = MaterialTheme.colorScheme.onPrimary
        ) {
            Icon(Icons.Filled.Add, contentDescription = "Add new note")
        }
    }

    Scaffold(
        floatingActionButton = {
            if (currentScreen == Screen.DASHBOARD || currentScreen == Screen.NOTES) {
                FloatingActionButton(
                    onClick = onNewNote,
                    containerColor = MaterialTheme.colorScheme.primary,
                    contentColor = MaterialTheme.colorScheme.onPrimary
                ) {
                    Icon(Icons.Filled.Add, contentDescription = "Add Note")
                }
            }
        },
        bottomBar = {
            @OptIn(ExperimentalMaterial3Api::class)
            NavigationBar {
                NavigationBarItem(
                    icon = { Icon(Icons.Filled.Dashboard, "Dashboard") },
                    label = { Text("Dashboard") },
                    selected = currentScreen == Screen.DASHBOARD,
                    onClick = { onScreenChange(Screen.DASHBOARD) }
                )
                NavigationBarItem(
                    icon = { Icon(Icons.AutoMirrored.Filled.Note, "Notes") },
                    label = { Text("Notes") },
                    selected = currentScreen == Screen.NOTES,
                    onClick = { onScreenChange(Screen.NOTES) }
                )
                NavigationBarItem(
                    icon = { Icon(Icons.Filled.Settings, "Settings") },
                    label = { Text("Settings") },
                    selected = currentScreen == Screen.SETTINGS,
                    onClick = { onScreenChange(Screen.SETTINGS) }
                )
            }
        }
    ) { paddingValues ->
        Box(modifier = Modifier
            .fillMaxSize()
            .padding(paddingValues)) {
            AnimatedContent(
                targetState = currentScreen,
                transitionSpec = {
                    when {
                        targetState == Screen.DASHBOARD -> {
                            (slideInHorizontally { width -> -width } + fadeIn()) togetherWith
                            (slideOutHorizontally { width -> width } + fadeOut())
                        }
                        initialState == Screen.DASHBOARD -> {
                            (slideInHorizontally { width -> width } + fadeIn()) togetherWith
                            (slideOutHorizontally { width -> -width } + fadeOut())
                        }
                        else -> {
                            (slideInHorizontally { width -> width } + fadeIn()) togetherWith
                            (slideOutHorizontally { width -> -width } + fadeOut())
                        }
                    }
                },
                label = "screen_transition"
            ) { screen ->
                when (screen) {
                Screen.DASHBOARD -> {
                    DashboardScreen(
                        viewModel = viewModel,
                        onNoteClick = onNoteClick,
                        onNewNote = onNewNote,
                        onSearch = { /* Navigate to search */ },
                        onCategoryClick = { category -> 
                            // Filter notes by category
                        },
                        onTagClick = { tag -> 
                            // Filter notes by tag
                        },
                        onTemplates = onShowTemplates,
                        onCategories = onShowCategories,
                        onShowAllNotes = { onScreenChange(Screen.NOTES) },
                        onShowFavorites = { onScreenChange(Screen.NOTES) },
                        onShowPinned = { onScreenChange(Screen.NOTES) },
                        onShowVault = { onScreenChange(Screen.NOTES) }
                    )
                }
                Screen.NOTES -> {
                    SavedNotesListScreen(
                        viewModel = viewModel,
                        onNoteClick = onNoteClick,
                        onNewNote = onNewNote
                    )
                }
                Screen.SETTINGS -> {
                    SettingsScreen(
                        repository = (context.applicationContext as AINoteBuddyApplication).advancedNoteRepository,
                        backupManager = BackupRestoreManager(
                            context = context,
                            repository = (context.applicationContext as AINoteBuddyApplication).advancedNoteRepository
                        ),
                        onSignOut = onSignOut,
                        onSyncToDrive = onSyncToDrive,
                        onSyncFromDrive = onSyncFromDrive,
                        onAISettingsClick = { onScreenChange(Screen.AI_SETTINGS) }
                    )
                }
                Screen.AI_SETTINGS -> {
                    AISettingsScreen(
                        onBackClick = { onScreenChange(Screen.SETTINGS) }
                    )
                }
                Screen.CALENDAR -> {
                    CalendarNotesScreen(
                        viewModel = viewModel,
                        onNoteClick = onNoteClick,
                        onBack = { onScreenChange(Screen.DASHBOARD) },
                        onNewNote = onNewNote
                    )
                }
                Screen.CANVAS -> {
                    InfiniteCanvasScreen(
                        onBack = { onScreenChange(Screen.DASHBOARD) },
                        onSave = { canvasState -> 
                            // Handle saving canvas state
                            onNewNote()
                        }
                    )
                }
                Screen.WEB_CLIPPER -> {
                    WebClipperScreen(
                        onBack = { onScreenChange(Screen.DASHBOARD) },
                        onClipSaved = { note -> 
                            // Handle saved clip
                            onNoteClick(note)
                        }
                    )
                }
                Screen.CLIPBOARD -> {
                    ClipboardManagerScreen(
                        onBack = { onScreenChange(Screen.DASHBOARD) },
                        onAddToNote = { content -> 
                            // Handle adding clipboard content to note
                            onNewNote()
                        }
                    )
                }
                Screen.TEMPLATES -> {
                    SmartTemplatesScreen(
                        onBack = { onScreenChange(Screen.DASHBOARD) },
                        onTemplateSelected = { template -> 
                            // Handle template selection - create new note from template
                            onNewNote()
                        }
                    )
                }
                Screen.VERSION_HISTORY -> {
                    Column(
                        modifier = Modifier.fillMaxSize().padding(16.dp),
                        horizontalAlignment = Alignment.CenterHorizontally,
                        verticalArrangement = Arrangement.Center
                    ) {
                        Text(
                            "Version History - Select a note to view its version history",
                            style = MaterialTheme.typography.headlineMedium,
                            textAlign = TextAlign.Center
                        )
                        Spacer(modifier = Modifier.height(16.dp))
                        Button(onClick = { onScreenChange(Screen.DASHBOARD) }) {
                            Text("Go Back")
                        }
                    }
                }
                }
            }
        }
    }
}

@Composable
fun SavedNotesListScreen(
    viewModel: NoteViewModel,
    onNoteClick: (NoteEntity) -> Unit,
    onNewNote: () -> Unit
) {
    val notes by viewModel.notes.collectAsState()

    Column(
        modifier = Modifier.fillMaxSize()
    ) {

        if (notes.isEmpty()) {
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(32.dp),
                horizontalAlignment = Alignment.CenterHorizontally,
                verticalArrangement = Arrangement.Center
            ) {
                Icon(
                    Icons.AutoMirrored.Filled.Note,
                    contentDescription = null,
                    modifier = Modifier.size(64.dp),
                    tint = MaterialTheme.colorScheme.onSurfaceVariant.copy(alpha = 0.6f)
                )
                Spacer(modifier = Modifier.height(16.dp))
                Text(
                    text = "No notes yet",
                    style = MaterialTheme.typography.headlineSmall,
                    color = MaterialTheme.colorScheme.onSurfaceVariant
                )
                Spacer(modifier = Modifier.height(8.dp))
                Text(
                    text = "Tap the + button to create your first note",
                    style = MaterialTheme.typography.bodyMedium,
                    color = MaterialTheme.colorScheme.onSurfaceVariant.copy(alpha = 0.7f),
                    textAlign = TextAlign.Center
                )
            }
        } else {
            LazyColumn(
                contentPadding = PaddingValues(16.dp),
                verticalArrangement = Arrangement.spacedBy(8.dp)
            ) {
                items(notes) { note ->
                    NoteListItem(
                        note = note,
                        onClick = { onNoteClick(note) },
                        onFavoriteClick = { viewModel.toggleFavorite(it.id) },
                        onPinClick = { viewModel.togglePin(it.id) }
                    )
                }
            }
        }
    }
}

@Composable
fun NoteListItem(
    note: NoteEntity,
    onClick: () -> Unit,
    onFavoriteClick: (NoteEntity) -> Unit = {},
    onPinClick: (NoteEntity) -> Unit = {}
) {
    Card(
        onClick = onClick,
        modifier = Modifier.fillMaxWidth(),
        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Text(
                text = note.title.ifBlank { "Untitled" },
                style = MaterialTheme.typography.titleMedium,
                fontWeight = FontWeight.Bold,
                maxLines = 1,
                overflow = TextOverflow.Ellipsis
            )

            if (note.content.isNotBlank()) {
                Spacer(modifier = Modifier.height(4.dp))
                Text(
                    text = note.content,
                    style = MaterialTheme.typography.bodyMedium,
                    color = MaterialTheme.colorScheme.onSurfaceVariant,
                    maxLines = 2,
                    overflow = TextOverflow.Ellipsis
                )
            }

            Spacer(modifier = Modifier.height(8.dp))

            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = formatDate(note.updatedAt),
                    style = MaterialTheme.typography.bodySmall,
                    color = MaterialTheme.colorScheme.onSurfaceVariant.copy(alpha = 0.7f)
                )

                Row(
                    horizontalArrangement = Arrangement.spacedBy(8.dp),
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    if (note.category.isNotBlank()) {
                        AssistChip(
                            onClick = { },
                            label = {
                                Text(
                                    text = note.category,
                                    style = MaterialTheme.typography.bodySmall
                                )
                            },
                            modifier = Modifier.height(24.dp)
                        )
                    }

                    IconButton(
                        onClick = { onPinClick(note) },
                        modifier = Modifier.size(32.dp)
                    ) {
                        Icon(
                            if (note.isPinned) Icons.Filled.Star else Icons.Filled.StarBorder,
                            contentDescription = if (note.isPinned) "Unpin" else "Pin",
                            tint = if (note.isPinned) Color(0xFFFFD700) else MaterialTheme.colorScheme.onSurfaceVariant,
                            modifier = Modifier.size(18.dp)
                        )
                    }

                    IconButton(
                        onClick = { onFavoriteClick(note) },
                        modifier = Modifier.size(32.dp)
                    ) {
                        Icon(
                            if (note.isFavorite) Icons.Filled.Favorite else Icons.Filled.FavoriteBorder,
                            contentDescription = if (note.isFavorite) "Remove from favorites" else "Add to favorites",
                            tint = if (note.isFavorite) Color(0xFFFC5C7D) else MaterialTheme.colorScheme.onSurfaceVariant,
                            modifier = Modifier.size(18.dp)
                        )
                    }
                }
            }
        }
    }
}

private fun formatDate(timestamp: Long): String {
    val sdf = java.text.SimpleDateFormat("MMM dd, yyyy", java.util.Locale.getDefault())
    return sdf.format(java.util.Date(timestamp))
}
